INSERT INTO `ge_tintroduccion` (`idintroduccion`, `parrafo`, `orden`) VALUES
(15, 'El Grupo de Robótica, Visión y Control (GRVC), ubicado en la Escuela Superior \r\n  de Ingenieros (Universidad de Sevilla), está compuesto por más de 25 investigadores. \r\n  Las principales líneas de investigación y desarrollo son la robótica, los sistemas \r\n  empotrados, la visión por computador y técnicas de control.', 1),
(16, 'El GRVC combina la actividad investigadora con el desarrollo de prototipos y la implementación de aplicaciones en colaboración con diversas empresas e instituciones.', 2),
(17, 'En los últimos 10 años el GRVC ha llevado a cabo 43 proyectos de investigación y desarrollo, y sus miembros han sido autores de más de 200 publicaciones, incluyendo tres libros.', 3),
(18, 'El Grupo está muy involucrado en los Programas Marco de la Comisión Europea, en los que ha participado como socio en 7 proyectos y ha liderado (Coordinación Científica y Técnica) el proyecto COMETS dentro del Programa IST.', 4),
(19, 'Además, los investigadores del GRVC han desarrollado 7 proyectos del Plan Nacional y liderado 3 proyectos coordinados.', 5),
(20, 'La colaboración y la transferencia de tecnología a empresas también tiene un papel importante en la actividad del grupo. De esta forma, varias tecnologías y sistemas desarrollados por el grupo están siendo utilizados por diversas empresas (EADS, IZAR, Michelín, Acuinova, Servicios Forestales,…) e instituciones como el Instituto Nacional de Técnicas Aeroespaciales (INTA).', 6),
(21, 'El GRVC coopera con un numeroso grupo de prestigiosas universidades, empresas y centros de investigación de Europa y Estados Unidos.', 7),
(22, 'El grupo oferta cada año becas para la realización de Proyectos Fin de Carrera y Tesis Doctorales.', 8),
(23, 'Sevilla,  Octubre de 2005.', 9);
INSERT INTO `li_tinvestigacion` (`idlinea`, `linea`, `campo`, `descripcion`) VALUES
(15, 'Robótica: Robots Móviles, Robótica Aérea y Telerobótica', 'Control de robots', 'Control de robots y vehículos autónomos incluyendo vehículos terrestres y aéreos (UAVs).'),
(16, 'Robótica: Robots Móviles, Robótica Aérea y Telerobótica', 'Percepción en robótica', 'Seguimiento visual robótico, percepción basada en sensores de proximetría (ultrasonidos, láser 2D, láser 3D).'),
