INSERT INTO `tnacional` (`idnacional`, `titulo`, `cabecera`, `cuerpo`, `anyo`, `mes`, `terminado`) VALUES 
(77, 'ROBINTER: Robot para inspección de interiores en estructuras aeronáuticas', 'Programa FEDER-INNTERCONECTA del CDTI, financiado por AIRBUS y APPLUS. PI-0986/2012.', '\r\n\r\n\r\n	\r\n	\r\n	\r\n	<style type="text/css">P { margin-bottom: 0cm; direction: ltr; color: rgb(0, 0, 0); text-align: justify; }P.western { font-family: &quot;Times New Roman&quot;,serif; font-size: 12pt; }P.cjk { font-family: &quot;Times New Roman&quot;,serif; font-size: 12pt; }P.ctl { font-family: &quot;Times New Roman&quot;,serif; font-size: 10pt; }A:link { color: rgb(0, 0, 255); }</style>\r\n\r\n\r\n<p align="JUSTIFY" style="text-indent: 0.95cm; margin-top: 0.21cm; margin-bottom: 0.21cm; widows: 2; orphans: 2;" class="western">\r\n<font face="Calibri, sans-serif"><font size="2">El proyecto ROBINTER<i>&nbsp;</i>está orientado a diseñar y desarrollar un sistema robótico que\r\npueda moverse de forma automática y teleoperada para labores de\r\ninspección, principalmente visual, dentro de espacios angostos y\r\nreducidos. Esta tarea trata principalmente de evitar la presencia\r\nhumana en dichos espacios debido al alto riesgo que supone tanto para\r\nlas personas como para la estructura del avión.</font></font></p>\r\n<p align="JUSTIFY" style="text-indent: 0.95cm; margin-top: 0.21cm; margin-bottom: 0.21cm; widows: 2; orphans: 2;" class="western">\r\n<font face="Calibri, sans-serif"><font size="2">La participación de\r\nAICIA en el proyecto consiste en el estudio, modelado, simulación y\r\ndesarrollo del sistema de control que permita al robot moverse\r\ncorrectamente dentro de distintas estructuras aeronáuticas.\r\nAsimismo, AICIA participa en el estudio y desarrollo de algoritmos de\r\nplanificación de trayectorias para la evitación de obstáculos\r\ndurante el movimiento del robot y en el diseño del interfaz HMI del\r\noperador del robot.</font></font></p>\r\n<p align="CENTER" style="text-indent: 1.25cm; margin-top: 0.21cm; margin-bottom: 0.21cm; widows: 2; orphans: 2;" class="western">\r\n<br />\r\n</p>\r\n<p align="CENTER" style="text-indent: 1.25cm; margin-top: 0.21cm; margin-bottom: 0.21cm; widows: 2; orphans: 2;" class="western">\r\n<font face="Calibri, sans-serif"><font size="2">ENTIDAD FINANCIADORA:\r\nAPPLUS y EADS-CASA (AIRBUS DEFENCE AND SPACE).</font></font></p>\r\n\r\n<br /><br />\r\n\r\n\r\n', 2012, 3, 0x30),
(78, 'ROBMAN: Sistema robótico inteligente para manipulación de grandes estructuras aeronáuticas', 'Programa FEDER-INNTERCONECTA del CDTI, financiado por MC2. PI-0985/2012.', '\r\n<!--[if gte mso 9]><xml>\r\n <o:OfficeDocumentSettings>\r\n  <o:TargetScreenSize>800x600</o:TargetScreenSize>\r\n </o:OfficeDocumentSettings>\r\n</xml><![endif]-->\r\n\r\n\r\n	\r\n	\r\n	\r\n	<style type="text/css">P.ep-western { text-indent: 0.95cm; margin-top: 0.21cm; margin-bottom: 0.21cm; font-family: &quot;Times New Roman&quot;,serif; font-size: 12pt; widows: 2; orphans: 2; }P.ep-cjk { text-indent: 0.95cm; margin-top: 0.21cm; margin-bottom: 0.21cm; font-family: &quot;Times New Roman&quot;,serif; font-size: 12pt; widows: 2; orphans: 2; }P.ep-ctl { text-indent: 0.95cm; margin-top: 0.21cm; margin-bottom: 0.21cm; font-family: &quot;Times New Roman&quot;,serif; font-size: 12pt; widows: 2; orphans: 2; }P { margin-bottom: 0cm; direction: ltr; color: rgb(0, 0, 0); text-align: justify; }P.western { font-family: &quot;Times New Roman&quot;,serif; font-size: 12pt; }P.cjk { font-family: &quot;Times New Roman&quot;,serif; font-size: 12pt; }P.ctl { font-family: &quot;Times New Roman&quot;,serif; font-size: 10pt; }A:link { color: rgb(0, 0, 255); }</style>\r\n\r\n\r\n<p lang="es-ES" class="ep-western"><font face="Calibri, sans-serif"><font size="2"><span lang="es-ES">En\r\neste proyecto se trata </span>diseñar y desarrollar un sistema\r\nautomatizado de elevación suspendido de un puente-grúa, que\r\npermitirá <span lang="es-ES">automatizar </span>el manejo y\r\ntransporte de distintos elementos de grandes dimensiones<span lang="es-ES">&nbsp;</span>de<span lang="es-ES"> un</span> avión </font></font>\r\n</p>\r\n<p lang="es-ES" class="ep-western"><font face="Calibri, sans-serif"><font size="2">La\r\nparticipación de AICIA en el proyecto consiste en diseñar y\r\ndesarrollar la algoritmia de control, el estudio estático y dinámico\r\nde todos los parámetros presentes y el modelado y simulación\r\nmediante software de un sistema para manipulación de elementos <span lang="es-ES">de\r\navión</span>.<span lang="es-ES"> En este año se ha desarrollado el\r\ncontrol y simulación del sistema de manipulación de piezas en 3D.</span></font></font></p>\r\n<p lang="es-ES" align="CENTER" style="text-indent: 1.25cm;" class="ep-western">\r\n<br />\r\n</p>\r\n<p lang="es-ES" align="CENTER" style="text-indent: 1.25cm;" class="ep-western">\r\n<font face="Calibri, sans-serif"><font size="2">ENTIDAD FINANCIADORA:\r\nMC2 y EADS-CASA<span lang="es-ES"> (</span>AIRBUS DEFENCE AND SPACE).</font></font></p>\r\n\r\n<span lang="ES" style="font-size: 10pt;"></span><!--[if gte mso 9]><xml>\r\n <w:WordDocument>\r\n  <w:View>Normal</w:View>\r\n  <w:Zoom>0</w:Zoom>\r\n  <w:TrackMoves/>\r\n  <w:TrackFormatting/>\r\n  <w:HyphenationZone>21</w:HyphenationZone>\r\n  <w:PunctuationKerning/>\r\n  <w:ValidateAgainstSchemas/>\r\n  <w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n  <w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n  <w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n  <w:DoNotPromoteQF/>\r\n  <w:LidThemeOther>ES-TRAD</w:LidThemeOther>\r\n  <w:LidThemeAsian>X-NONE</w:LidThemeAsian>\r\n  <w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript>\r\n  <w:Compatibility>\r\n   <w:BreakWrappedTables/>\r\n   <w:SnapToGridInCell/>\r\n   <w:WrapTextWithPunct/>\r\n   <w:UseAsianBreakRules/>\r\n   <w:DontGrowAutofit/>\r\n   <w:SplitPgBreakAndParaMark/>\r\n   <w:EnableOpenTypeKerning/>\r\n   <w:DontFlipMirrorIndents/>\r\n   <w:OverrideTableStyleHps/>\r\n  </w:Compatibility>\r\n  <w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel>\r\n  <m:mathPr>\r\n   <m:mathFont m:val="Cambria Math"/>\r\n   <m:brkBin m:val="before"/>\r\n   <m:brkBinSub m:val="--"/>\r\n   <m:smallFrac m:val="off"/>\r\n   <m:dispDef/>\r\n   <m:lMargin m:val="0"/>\r\n   <m:rMargin m:val="0"/>\r\n   <m:defJc m:val="centerGroup"/>\r\n   <m:wrapIndent m:val="1440"/>\r\n   <m:intLim m:val="subSup"/>\r\n   <m:nary